 // DEPENDENCIES
import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';
import { map } from 'rxjs/operators';
import { HttpClient } from '@angular/common/http';

// CONFIG
import { environment } from '../../../environments/environment';

// MODEL
import { ProcessFlow } from '../../domain/easy-recruit-demo_db/process-flow';

/**
 * THIS SERVICE MAKE HTTP REQUEST TO SERVER, FOR CUSTOMIZE IT EDIT ../ProcessFlow.service.ts
 */

/*
 * SCHEMA DB ProcessFlow
 *
	{
		Action: {
			type: 'String'
		},
		Completed: {
			type: 'Boolean'
		},
		NextState: {
			type: 'String'
		},
		State: {
			type: 'String'
		},
		//RELATIONS
		//EXTERNAL RELATIONS
	}
 *
 */
@Injectable()
export class ProcessFlowBaseService {

    contextUrl: string = environment.endpoint + '/processflow';
    constructor(
        protected http: HttpClient
        ) { }

    // CRUD METHODS

    /**
    * ProcessFlowService.create
    *   @description CRUD ACTION create
    *
    */
    create(item: ProcessFlow): Observable<ProcessFlow> {
        return this.http
            .post<ProcessFlow>(this.contextUrl, item)
            .pipe(map(data => data));
    }

    /**
    * ProcessFlowService.delete
    *   @description CRUD ACTION delete
    *   @param ObjectId id Id
    *
    */
    remove(id: string): Observable<void> {
        return this.http
            .delete<void>(this.contextUrl + '/' + id)
            .pipe(map(data => data));
    }

    /**
    * ProcessFlowService.get
    *   @description CRUD ACTION get
    *   @param ObjectId id Id resource
    *
    */
    get(id: string): Observable<ProcessFlow> {
        return this.http
            .get<ProcessFlow>(this.contextUrl + '/' + id)
            .pipe(map(data => data));
    }

    /**
    * ProcessFlowService.list
    *   @description CRUD ACTION list
    *
    */
    list(): Observable<ProcessFlow[]> {
        return this.http
            .get<ProcessFlow[]>(this.contextUrl)
            .pipe(map(data => data));
    }

    /**
    * ProcessFlowService.update
    *   @description CRUD ACTION update
    *   @param ObjectId id Id
    *
    */
    update(item: ProcessFlow): Observable<ProcessFlow> {
        return this.http
            .post<ProcessFlow>(this.contextUrl + '/' + item._id, item)
            .pipe(map(data => data));
    }


    // Custom APIs

}
